import { useState, useEffect } from "react";
import styles from "./style.module.css";

export default function Daily() {
  const [source, setSource] = useState("");
  const [amount, setAmount] = useState("");
  const [reference, setReference] = useState("");
  const [date, setDate] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [editId, setEditId] = useState(null);
  const [editSource, setEditSource] = useState("");
  const [editAmount, setEditAmount] = useState("");
  const [editReference, setEditReference] = useState("");
  const [editDate, setEditDate] = useState("");

  const apiUrl = "http://localhost:8080/api";

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = () => {
    fetch(`${apiUrl}/dailyexpense`, { method: "GET" })
      .then((res) => res.json())
      .then((res) => setExpenses(res))
      .catch(() => setError("Failed to fetch daily expenses"));
  };

  const handleSubmit = () => {
    setError("");
    if (!source.trim() || !amount.trim() || !date.trim()) {
      setError("Source, Amount, and Date are required.");
      return;
    }

    fetch(`${apiUrl}/dailyexpense`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ source, amount: Number(amount), reference, date }),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Daily expense added successfully");
        setSource("");
        setAmount("");
        setReference("");
        setDate("");
        setTimeout(() => setMessage(""), 3000);
        fetchExpenses();
      })
      .catch(() => setError("Unable to create expense entry"));
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this?")) {
      fetch(`${apiUrl}/dailyexpense/${id}`, { method: "DELETE" })
        .then(() => {
          setMessage("Expense deleted successfully");
          setTimeout(() => setMessage(""), 3000);
          fetchExpenses();
        })
        .catch(() => setError("Unable to delete expense entry"));
    }
  };

  const handleEdit = (item) => {
    setEditId(item._id);
    setEditSource(item.source);
    setEditAmount(item.amount);
    setEditReference(item.reference);
    setEditDate(item.date);
  };

  const handleUpdate = () => {
    setError("");
    if (!editSource.trim() || !editAmount.toString().trim() || !editDate.trim()) {
      setError("Source, Amount, and Date are required.");
      return;
    }

    fetch(`${apiUrl}/dailyexpense/${editId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        source: editSource,
        amount: Number(editAmount),
        reference: editReference,
        date: editDate,
      }),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Daily expense updated successfully");
        setTimeout(() => setMessage(""), 3000);
        setEditId(null);
        fetchExpenses();
      })
      .catch(() => setError("Unable to update expense entry"));
  };

  const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);

  return (
    <div>
      <div className={styles.container}>
        <h3>{editId ? "Edit Daily Expense" : "Add Daily Expense"}</h3>
        {message && <div className={styles.success_msg}>{message}</div>}
        <div className={styles.formGroup}>
          <input
            placeholder="Expense Source"
            value={editId ? editSource : source}
            onChange={(e) =>
              editId ? setEditSource(e.target.value) : setSource(e.target.value)
            }
            className={styles.formControl}
          />
          <input
            type="number"
            placeholder="Amount"
            value={editId ? editAmount : amount}
            onChange={(e) =>
              editId ? setEditAmount(e.target.value) : setAmount(e.target.value)
            }
            className={styles.formControl}
          />
          <input
            placeholder="Reference (optional)"
            value={editId ? editReference : reference}
            onChange={(e) =>
              editId ? setEditReference(e.target.value) : setReference(e.target.value)
            }
            className={styles.formControl}
          />
          <input
            type="date"
            value={editId ? editDate : date}
            onChange={(e) =>
              editId ? setEditDate(e.target.value) : setDate(e.target.value)
            }
            className={styles.formControl}
          />
          {editId ? (
            <div className={styles.buttonGroup}>
              <button className={styles.btnSuccess} onClick={handleUpdate}>
                Update
              </button>
              <button className={styles.btnSecondary} onClick={() => setEditId(null)}>
                Cancel
              </button>
            </div>
          ) : (
            <button className={styles.btnDark} onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
        {error && <div className={styles.error_msg}>{error}</div>}
      </div>
      <div className={styles.tableContainer}>
        <h3>Daily Expense List</h3>
        <table className={styles.table}>
          <thead>
            <tr>
              <th>Expense Source</th>
              <th>Amount</th>
              <th>Reference</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {expenses.map((item) => (
              <tr key={item._id}>
                <td>{item.source}</td>
                <td>${item.amount}</td>
                <td>{item.reference || "N/A"}</td>
                <td>{item.date}</td>
                <td>
                  <button className={styles.btnWarning} onClick={() => handleEdit(item)}>
                    Edit
                  </button>
                  <button className={styles.btnDanger} onClick={() => handleDelete(item._id)}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className={styles.totalExpense}>
        <h4>Total Daily Expense: ${totalExpense.toFixed(2)}</h4>
      </div>
    </div>
  );
}
