import { useState, useEffect } from "react";
import axios from "axios";

const API_URL = "http://localhost:8080/api/income"; // Update this if needed

function Income() {
  const [amount, setAmount] = useState("");
  const [source, setSource] = useState("");
  const [date, setDate] = useState("");
  const [incomes, setIncomes] = useState([]);

  const token = localStorage.getItem("token"); // Get JWT from localStorage

  useEffect(() => {
    if (!token) return;

    const fetchIncome = async () => {
      try {
        const response = await axios.get(`${API_URL}/income`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setIncomes(response.data);
      } catch (error) {
        console.error("Error fetching income:", error);
      }
    };

    fetchIncome();
  }, [token]);

  const handleAddIncome = async (e) => {
    e.preventDefault();
    if (!amount || !source || !date) {
      alert("Please fill all fields");
      return;
    }

    try {
      const response = await axios.post(
        `${API_URL}/incomeAdd`,
        { amount, source, date },
        { headers: { Authorization: `Bearer ${token}`, "Content-Type": "application/json" } }
      );

      setIncomes([...incomes, response.data]); // Update state
      setAmount("");
      setSource("");
      setDate("");
    } catch (error) {
      console.error("Error adding income:", error);
    }
  };

  return (
    <div>
      <h1>Income Tracker</h1>

      <h2>Add Income</h2>
      <form onSubmit={handleAddIncome}>
        <input type="number" placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} required />
        <input type="text" placeholder="Source" value={source} onChange={(e) => setSource(e.target.value)} required />
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        <button type="submit">Add Income</button>
      </form>

      <h2>Your Incomes</h2>
      <ul>
        {incomes.length === 0 ? (
          <p>No incomes added yet.</p>
        ) : (
          incomes.map((income) => (
            <li key={income._id}>
              {income.source} - â‚¹{income.amount} on {new Date(income.date).toLocaleDateString()}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default Income;
