import { useState, useEffect } from "react";
import Navigation from "../NavBar";
import styles from "./styles.module.css"; // CSS Module for styling

export default function Budget() {
  const [budgetName, setBudgetName] = useState("");
  const [amount, setAmount] = useState("");
  const [reference, setReference] = useState("");
  const [date, setDate] = useState("");
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // Editing state
  const [editId, setEditId] = useState(null);
  const [editBudgetName, setEditBudgetName] = useState("");
  const [editAmount, setEditAmount] = useState("");
  const [editReference, setEditReference] = useState("");
  const [editDate, setEditDate] = useState("");

  // Change the API URL as needed.
  const apiUrl = "http://localhost:8080/api";

  useEffect(() => {
    fetchBudgets();
  }, []);

  // Fetch budgets from backend
  const fetchBudgets = () => {
    fetch(`${apiUrl}/budget`, { method: "GET" })
      .then((res) => res.json())
      .then((data) => setBudgets(data))
      .catch(() => setError("Failed to fetch budgets"));
  };

  // Create a new budget
  const handleSubmit = () => {
    setError("");
    if (!budgetName.trim() || !amount.trim()) {
      setError("Budget name and amount are required.");
      return;
    }
    const payload = { budgetName, amount: Number(amount), reference, date };

    fetch(`${apiUrl}/budget`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Budget created successfully");
        setBudgetName("");
        setAmount("");
        setReference("");
        setDate("");
        setTimeout(() => setMessage(""), 3000);
        fetchBudgets();
      })
      .catch(() => setError("Unable to create budget"));
  };

  // Delete a budget
  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this budget?")) {
      fetch(`${apiUrl}/budget/${id}`, { method: "DELETE" })
        .then(() => {
          setMessage("Budget deleted successfully");
          setTimeout(() => setMessage(""), 3000);
          fetchBudgets();
        })
        .catch(() => setError("Unable to delete budget"));
    }
  };

  // Set editing state for the selected budget
  const handleEdit = (budget) => {
    setEditId(budget._id);
    setEditBudgetName(budget.budgetName);
    setEditAmount(budget.amount);
    setEditReference(budget.reference);
    setEditDate(budget.date);
  };

  // Update the budget
  const handleUpdate = () => {
    setError("");
    if (!editBudgetName.trim() || !editAmount.toString().trim()) {
      setError("Budget name and amount are required.");
      return;
    }
    const payload = {
      budgetName: editBudgetName,
      amount: Number(editAmount),
      reference: editReference,
      date: editDate,
    };

    fetch(`${apiUrl}/budget/${editId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Budget updated successfully");
        setTimeout(() => setMessage(""), 3000);
        setEditId(null);
        fetchBudgets();
      })
      .catch(() => setError("Unable to update budget"));
  };

  // Calculate total budget amount
  const totalBudget = budgets.reduce((sum, budget) => sum + budget.amount, 0);

  return (
    <div>
      <Navigation />
      <div className={styles.container}>
        <div className={styles.formContainer}>
          <h3>{editId ? "Edit Budget" : "Create Budget"}</h3>
          {message && <div className={styles.success_msg}>{message}</div>}
          <div className={styles.formGroup}>
            {editId ? (
              <input
                type="text"
                placeholder="Budget Name"
                value={editBudgetName}
                onChange={(e) => setEditBudgetName(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="text"
                placeholder="Budget Name"
                value={budgetName}
                onChange={(e) => setBudgetName(e.target.value)}
                className={styles.formControl}
              />
            )}

            {editId ? (
              <input
                type="number"
                placeholder="Amount"
                value={editAmount}
                onChange={(e) => setEditAmount(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="number"
                placeholder="Amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                className={styles.formControl}
              />
            )}

            {editId ? (
              <input
                type="text"
                placeholder="Reference (optional)"
                value={editReference}
                onChange={(e) => setEditReference(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="text"
                placeholder="Reference (optional)"
                value={reference}
                onChange={(e) => setReference(e.target.value)}
                className={styles.formControl}
              />
            )}

            {editId ? (
              <input
                type="date"
                value={editDate}
                onChange={(e) => setEditDate(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className={styles.formControl}
              />
            )}

            {editId ? (
              <div className={styles.buttonGroup}>
                <button className={styles.btnSuccess} onClick={handleUpdate}>
                  Update
                </button>
                <button className={styles.btnSecondary} onClick={() => setEditId(null)}>
                  Cancel
                </button>
              </div>
            ) : (
              <button className={styles.btnDark} onClick={handleSubmit}>
                Create Budget
              </button>
            )}
          </div>
          {error && <div className={styles.error_msg}>{error}</div>}
        </div>

        <div className={styles.tableContainer}>
          <h3>Budget List</h3>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>Budget Name</th>
                <th>Amount</th>
                <th>Reference</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {budgets.map((budget) => (
                <tr key={budget._id}>
                  <td>{budget.budgetName}</td>
                  <td>${budget.amount}</td>
                  <td>{budget.reference || "N/A"}</td>
                  <td>{new Date(budget.date).toLocaleDateString()}</td>
                  <td>
                    <button className={styles.btnWarning} onClick={() => handleEdit(budget)}>
                      Edit
                    </button>
                    <button className={styles.btnDanger} onClick={() => handleDelete(budget._id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className={styles.totalBudget}>
          <h4>Total Budget: ${totalBudget.toFixed(2)}</h4>
        </div>
      </div>
    </div>
  );
}
