import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [visitedDates, setVisitedDates] = useState([]);
  const today = new Date().toISOString().split('T')[0]; // Current date in YYYY-MM-DD format

  useEffect(() => {
    // Fetch visited dates
    const fetchVisitedDates = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/visited-dates');
        setVisitedDates(res.data.map((visit) => visit.date));
      } catch (err) {
        console.error('Error fetching visited dates:', err);
      }
    };

    // Save today's visit
    const saveVisit = async () => {
      try {
        await axios.post('http://localhost:5000/api/visit', { date: today });
      } catch (err) {
        console.error('Error saving visit:', err);
      }
    };

    saveVisit();
    fetchVisitedDates();
  }, [today]);

  return (
    <div className="App">
      <h1>Calendar</h1>
      <div className="calendar">
        {[...Array(31).keys()].map((day) => {
          const date = `2025-01-${String(day + 1).padStart(2, '0')}`;
          const isToday = date === today;
          const isVisited = visitedDates.includes(date);

          return (
            <div
              key={date}
              className={`day ${isToday ? 'today' : ''} ${isVisited ? 'visited' : ''}`}
            >
              {day + 1}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
