import React, { useState, useEffect } from "react";
import styles from "./styles.module.css";
import Navigation from "../NavBar";

// Options for the plan name and their default icons
const availableNames = [
  "transportation",
  "utilities",
  "entertainment",
  "shopping",
  "groceries",
  "other",
];

const availableIcons = {
  transportation: "🚗",
  utilities: "💡",
  entertainment: "🎉",
  shopping: "🛍️",
  groceries: "🍎",
  other: "📦",
};

const BudgetPlan = () => {
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // Create form state (only Name, Total, and Icon)
  const [name, setName] = useState(availableNames[0]);
  const [total, setTotal] = useState("");
  const [icon, setIcon] = useState(availableIcons[availableNames[0]]);
  const [showIconPicker, setShowIconPicker] = useState(false);

  // Edit form state (only Name, Total, and Icon)
  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState(availableNames[0]);
  const [editTotal, setEditTotal] = useState("");
  const [editIcon, setEditIcon] = useState(availableIcons[availableNames[0]]);
  const [showEditIconPicker, setShowEditIconPicker] = useState(false);

  const apiUrl = "http://localhost:8080/api";

  // Helper: get the auth token from localStorage
  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    };
  };

  useEffect(() => {
    fetchBudgets();
  }, []);

  const fetchBudgets = async () => {
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        headers: getAuthHeaders(),
      });
      if (!res.ok) throw new Error("Failed to fetch budget plans");
      const data = await res.json();
      setBudgets(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSubmit = async () => {
    if (!name || !total) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        method: "POST",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name,
          total: Number(total),
          spent: 0, // default value
          items: 0, // default value
          icon: icon,
        }),
      });
      if (!res.ok) throw new Error("Failed to create budget plan");
      setMessage("Budget plan created successfully!");
      // Reset form fields to defaults
      setName(availableNames[0]);
      setTotal("");
      setIcon(availableIcons[availableNames[0]]);
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this budget plan?")) {
      try {
        const res = await fetch(`${apiUrl}/budgetplan/${id}`, {
          method: "DELETE",
          headers: getAuthHeaders(),
        });
        if (!res.ok) throw new Error("Failed to delete budget plan");
        setMessage("Budget plan deleted successfully");
        fetchBudgets();
        setTimeout(() => setMessage(""), 3000);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleEdit = (plan) => {
    setEditId(plan._id);
    setEditName(plan.name);
    setEditTotal(plan.total);
    setEditIcon(plan.icon);
  };

  const handleUpdate = async () => {
    if (!editName || !editTotal) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan/${editId}`, {
        method: "PUT",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name: editName,
          total: Number(editTotal),
          spent: 0, // default value remains
          items: 0,
          icon: editIcon,
        }),
      });
      if (!res.ok) throw new Error("Failed to update budget plan");
      setMessage("Budget plan updated successfully");
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
      setEditId(null);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <>
      <Navigation />
      <div className={styles.container}>
        {/* Create Budget Form */}
        <div className={styles.formContainer}>
          <h1>Budget Plans</h1>
          {message && <div className={styles.success_msg}>{message}</div>}
          {error && <div className={styles.error_msg}>{error}</div>}
          <div className={styles.formGroup}>
            {/* Name Dropdown */}
            <select
              className={styles.formControl}
              value={name}
              onChange={(e) => {
                const newName = e.target.value;
                setName(newName);
                // Automatically update icon based on the selected name
                setIcon(availableIcons[newName]);
              }}
            >
              {availableNames.map((option) => (
                <option key={option} value={option}>
                  {option.charAt(0).toUpperCase() + option.slice(1)}
                </option>
              ))}
            </select>
            {/* Total Input */}
            <input
              type="number"
              className={styles.formControl}
              placeholder="Total"
              value={total}
              onChange={(e) => setTotal(e.target.value)}
            />
            {/* Icon Picker */}
            <div className={styles.iconPickerWrapper}>
              <input
                type="text"
                readOnly
                className={styles.formControl}
                placeholder="Select Icon"
                value={icon}
                onClick={() => setShowIconPicker(!showIconPicker)}
              />
              {showIconPicker && (
                <div className={styles.iconPicker}>
                  {Object.entries(availableIcons).map(([key, icn]) => (
                    <button
                      key={key}
                      type="button"
                      className={styles.iconButton}
                      onClick={() => {
                        setIcon(icn);
                        setShowIconPicker(false);
                      }}
                    >
                      {icn}
                    </button>
                  ))}
                </div>
              )}
            </div>
          </div>
          <div className={styles.buttonGroup}>
            <button
              type="button"
              className={styles.btnSuccess}
              onClick={handleSubmit}
            >
              Create Plan
            </button>
          </div>
        </div>

        {/* Display Budget Cards */}
        {budgets.length === 0 ? (
          <p>No budget plans found.</p>
        ) : (
          <div className={styles.budgetGrid}>
            {budgets.map((plan) => (
              <div key={plan._id} className={styles.budgetCard}>
                <div className={styles.budgetHeader}>
                  <span className={styles.icon}>{plan.icon}</span>
                  <span className={styles.budgetTitle}>
                    {plan.name.charAt(0).toUpperCase() + plan.name.slice(1)}
                  </span>
                  <span className={styles.amount}>${plan.total}</span>
                </div>
                <p className={styles.budgetInfo}>
                  ${plan.spent} Spent | ${plan.total - plan.spent} Remaining
                </p>
                <div className={styles.cardButtons}>
                  <button
                    className={styles.btnWarning}
                    onClick={() => handleEdit(plan)}
                  >
                    Edit
                  </button>
                  <button
                    className={styles.btnDanger}
                    onClick={() => handleDelete(plan._id)}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Edit Budget Form (only displays Name, Total, and Icon) */}
        {editId && (
          <div className={styles.formContainer}>
            <h2>Edit Budget Plan</h2>
            <div className={styles.formGroup}>
              {/* Edit Name Dropdown */}
              <select
                className={styles.formControl}
                value={editName}
                onChange={(e) => {
                  const newName = e.target.value;
                  setEditName(newName);
                  setEditIcon(availableIcons[newName]);
                }}
              >
                {availableNames.map((option) => (
                  <option key={option} value={option}>
                    {option.charAt(0).toUpperCase() + option.slice(1)}
                  </option>
                ))}
              </select>
              {/* Edit Total Input */}
              <input
                type="number"
                className={styles.formControl}
                placeholder="Total"
                value={editTotal}
                onChange={(e) => setEditTotal(e.target.value)}
              />
              {/* Edit Icon Picker */}
              <div className={styles.iconPickerWrapper}>
                <input
                  type="text"
                  readOnly
                  className={styles.formControl}
                  placeholder="Select Icon"
                  value={editIcon}
                  onClick={() => setShowEditIconPicker(!showEditIconPicker)}
                />
                {showEditIconPicker && (
                  <div className={styles.iconPicker}>
                    {Object.entries(availableIcons).map(([key, icn]) => (
                      <button
                        key={key}
                        type="button"
                        className={styles.iconButton}
                        onClick={() => {
                          setEditIcon(icn);
                          setShowEditIconPicker(false);
                        }}
                      >
                        {icn}
                      </button>
                    ))}
                  </div>
                )}
              </div>
            </div>
            <div className={styles.buttonGroup}>
              <button
                type="button"
                className={styles.btnSuccess}
                onClick={handleUpdate}
              >
                Save
              </button>
              <button
                type="button"
                className={styles.btnSecondary}
                onClick={() => setEditId(null)}
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default BudgetPlan;
