// BudgetBarChart.jsx
import { useState, useEffect } from "react";
import styles from "./style.module.css";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register necessary ChartJS components for a Bar chart
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function BudgetBarChart() {
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");

  // API base URL (adjust as needed)
  const apiUrl = "http://localhost:8080/api";
  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    };
  };

  // Fetch budget plans for the authenticated user
  useEffect(() => {
    fetchBudgets();
  }, []);

  const fetchBudgets = async () => {
    try {
      const res = await fetch(`${apiUrl}/budgetplan`,{
        headers: getAuthHeaders(),
      });
      if (!res.ok) {
        throw new Error("Failed to fetch budget data");
      }
      const data = await res.json();
      console.log("Budget Data:", data);
      setBudgets(data);
    } catch (err) {
      console.error("Error fetching budget data:", err);
      setError(err.message);
    }
  };

  // Prepare chart data:
  // - labels: budget plan names
  // - spentData: amount spent per budget
  // - remainingData: remaining budget calculated as total - spent
  const labels = budgets.map((budget) => budget.name);
  const spentData = budgets.map((budget) => budget.spent);
  const remainingData = budgets.map((budget) => budget.total - budget.spent);

  const chartData = {
    labels,
    datasets: [
      {
        label: "Spent",
        data: spentData,
        backgroundColor: "#ffcc00", // darker yellow for spending
        borderColor: "#e6a800",
        borderWidth: 1,
        stack: "stack1",
      },
      {
        label: "Remaining Budget",
        data: remainingData,
        backgroundColor: "#ffeb99", // lighter yellow for remaining
        borderColor: "#ffcc00",
        borderWidth: 1,
        stack: "stack1",
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // allow custom height
    plugins: {
      title: {
        display: true,
        text: "Budget vs Spending Chart",
      },
      tooltip: {
        enabled: true,
      },
      legend: {
        position: "top",
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        stacked: true,
      },
      x: {
        stacked: true,
      },
    },
  };

  return (
    <div className={styles.chartContainer}>
      {error && <p className={styles.error_msg}>{error}</p>}
      {/* Wrap the Bar chart in a div with a fixed height */}
      <div style={{ height: "400px" }}>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
}
