// BudgetPlan.jsx (Frontend)
import React, { useState, useEffect } from "react";
import styles from "./styles.module.css";
import Navigation from "../NavBar";

const BudgetPlan = () => {
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // Form state for creating a new budget plan
  const [name, setName] = useState("");
  const [total, setTotal] = useState("");
  const [spent, setSpent] = useState("");
  const [items, setItems] = useState("");
  const [icon, setIcon] = useState("");

  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editTotal, setEditTotal] = useState("");
  const [editSpent, setEditSpent] = useState("");
  const [editItems, setEditItems] = useState("");
  const [editIcon, setEditIcon] = useState("");

  const apiUrl = "http://localhost:8080/api";

  // Helper to get the auth token from localStorage
  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    };
  };

  useEffect(() => {
    fetchBudgets();
  }, []);

  const fetchBudgets = async () => {
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        headers: getAuthHeaders(),
      });
      if (!res.ok) throw new Error("Failed to fetch budget plans");
      const data = await res.json();
      setBudgets(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSubmit = async () => {
    if (!name || !total) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        method: "POST",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name,
          total: Number(total),
          spent: Number(spent) || 0,
          items: Number(items) || 0,
          icon: icon || "ðŸ’°",
        }),
      });
      if (!res.ok) throw new Error("Failed to create budget plan");
      setMessage("Budget plan created successfully!");
      // Clear form fields
      setName("");
      setTotal("");
      setSpent("");
      setItems("");
      setIcon("");
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this budget plan?")) {
      try {
        const res = await fetch(`${apiUrl}/budgetplan/${id}`, {
          method: "DELETE",
          headers: getAuthHeaders(),
        });
        if (!res.ok) throw new Error("Failed to delete budget plan");
        setMessage("Budget plan deleted successfully");
        fetchBudgets();
        setTimeout(() => setMessage(""), 3000);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleEdit = (plan) => {
    setEditId(plan._id);
    setEditName(plan.name);
    setEditTotal(plan.total);
    setEditSpent(plan.spent);
    setEditItems(plan.items);
    setEditIcon(plan.icon);
  };

  const handleUpdate = async () => {
    if (!editName || !editTotal) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan/${editId}`, {
        method: "PUT",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name: editName,
          total: Number(editTotal),
          spent: Number(editSpent) || 0,
          items: Number(editItems) || 0,
          icon: editIcon || "ðŸ’°",
        }),
      });
      if (!res.ok) throw new Error("Failed to update budget plan");
      setMessage("Budget plan updated successfully");
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
      setEditId(null); // Reset edit form
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className={styles.wrapper}>
      <Navigation />
      <div className={styles.container}>
        <h1 className={styles.title}>Budget Plans</h1>
        {message && <div className={styles.message}>{message}</div>}
        {error && <div className={styles.error}>{error}</div>}

        <form>
          <div>
            <label>Name</label>
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div>
            <label>Total</label>
            <input
              type="number"
              value={total}
              onChange={(e) => setTotal(e.target.value)}
            />
          </div>
          <div>
            <label>Spent</label>
            <input
              type="number"
              value={spent}
              onChange={(e) => setSpent(e.target.value)}
            />
          </div>
          <div>
            <label>Items</label>
            <input
              type="number"
              value={items}
              onChange={(e) => setItems(e.target.value)}
            />
          </div>
          <div>
            <label>Icon</label>
            <input
              type="text"
              value={icon}
              onChange={(e) => setIcon(e.target.value)}
            />
          </div>
          <button type="button" onClick={handleSubmit}>Create Plan</button>
        </form>

        {budgets.length === 0 && <p>No budget plans found.</p>}

        <ul>
          {budgets.map((plan) => (
            <li key={plan._id}>
              <span>{plan.name}</span> - <span>{plan.total}</span>
              <button onClick={() => handleEdit(plan)}>Edit</button>
              <button onClick={() => handleDelete(plan._id)}>Delete</button>
            </li>
          ))}
        </ul>

        {editId && (
          <div>
            <h2>Edit Budget Plan</h2>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
            />
            <input
              type="number"
              value={editTotal}
              onChange={(e) => setEditTotal(e.target.value)}
            />
            <button onClick={handleUpdate}>Save</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default BudgetPlan;
