import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import Footer from "../footer/index";
import Cal from "../Calendar/cal";
import Pie from "../piechart/Pie";
import Line from "../Linegraph/Line";
import Navigation from "../NavBar/index";
import Bar from "../Bargraph/bargraph";

const Main = () => {
  const apiUrl = "http://localhost:8080";
  const [incomes, setIncomes] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchIncomes();
    fetchExpenses();
  }, []);

  const fetchIncomes = () => {
    fetch(`${apiUrl}/income`)
      .then((res) => res.json())
      .then((data) => setIncomes(data))
      .catch(() => setError("Failed to fetch incomes"));
  };

  const fetchExpenses = () => {
    fetch(`${apiUrl}/api/expense`)
      .then((res) => res.json())
      .then((data) => setExpenses(data))
      .catch((err) => console.error("Error fetching expenses:", err));
  };

  // Calculate totals: Note that incomes use "Amount" (capital A) and expenses use "amount"
  const totalIncome = incomes.reduce((sum, item) => sum + item.Amount, 0);
  const totalExpense = expenses.reduce((sum, expense) => sum + expense.amount, 0);
  const remainingBalance = totalIncome - totalExpense;

  return (
    <div>
      <Navigation />
      <div className={styles.main_container}>
        <div className={styles.con}>
          <div className={styles.money}>
            <div className={styles.Total}>
              <h2>
                Total Income:{" "}
                <span className={styles.num}>${totalIncome.toFixed(2)}</span>
              </h2>
              <h2>
                Total Expense:{" "}
                <span className={styles.num2}>${totalExpense.toFixed(2)}</span>
              </h2>
            </div>
            <br />
            <div className={styles.remain}>
              <h2>
                Remaining:{" "}
                <span className={styles.num3}>${remainingBalance.toFixed(2)}</span>
              </h2>
            </div>
          </div>
          <br />
          <div className={styles.piechart}>
            <Pie />
          </div>
          <br />
          <div className={styles.cal}>
            <Cal />
          </div>
        </div>
        <div className={styles.line}>
          <Line />
        </div>
        <div className={styles.line}>
          <Bar />
        </div>
        <div className={styles.foot}>
          <Footer />
        </div>
      </div>
    </div>
  );
};

export default Main;
