import { useState, useEffect } from "react"; 
import styles from "./style.module.css";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Daily() {
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");

  // API base URL
  const apiUrl = "http://localhost:8080/api";

  // Helper: attach auth header (if token is required)
  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`
    };
  };

  useEffect(() => {
    fetchExpenses();
  }, []);

  // Fetch expenses (protected endpoint) and filter to only include "Daily" expenses
  const fetchExpenses = async () => {
    try {
      const res = await fetch(`${apiUrl}/expense`, { headers: getAuthHeaders() });
      if (!res.ok) {
        throw new Error("Failed to fetch expenses");
      }
      const data = await res.json();
      console.log("Fetched expenses:", data);
      // Filter for expenses where expenseType === "Daily"
      const dailyExpenses = data.filter(expense => expense.expenseType === "Daily");
      console.log("Filtered daily expenses:", dailyExpenses);
      setExpenses(dailyExpenses);
    } catch (err) {
      console.error("Error fetching expenses:", err);
      setError(err.message || "Failed to fetch expenses");
    }
  };

  // Compute the current week's Monday and return an array of 7 ISO date strings (Monday to Sunday)
  const getCurrentWeekDates = () => {
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Sun) ... 6 (Sat)
    // Calculate Monday (if today is Sunday, go back 6 days)
    const monday = new Date(today);
    monday.setDate(today.getDate() - ((dayOfWeek + 6) % 7));

    const weekDates = [];
    for (let i = 0; i < 7; i++) {
      const currentDate = new Date(monday);
      currentDate.setDate(monday.getDate() + i);
      weekDates.push(currentDate.toISOString().split("T")[0]);
    }
    return weekDates;
  };

  // Generate week labels and aggregate data from the daily expenses
  const getWeekData = () => {
    const weekDates = getCurrentWeekDates();

    // Sum expenses for each day of the week.
    const weekData = weekDates.map(dateStr => {
      return expenses.reduce((sum, expense) => {
        // Convert expense.date to ISO format for accurate comparison.
        const expenseDate = new Date(expense.date).toISOString().split("T")[0];
        return expenseDate === dateStr ? sum + expense.amount : sum;
      }, 0);
    });

    // Format labels as "Day(dd|mm|yyyy)"
    const dayNames = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
    const weekLabels = weekDates.map((dateStr, index) => {
      const d = new Date(dateStr);
      const day = d.getDate();
      const month = String(d.getMonth() + 1).padStart(2, '0');
      const year = d.getFullYear();
      return `${dayNames[index]}(${day}|${month}|${year})`;
    });

    console.log("Week Labels:", weekLabels);
    console.log("Week Data:", weekData);

    return { weekLabels, weekData };
  };

  const { weekLabels, weekData } = getWeekData();

  const chartData = {
    labels: weekLabels,
    datasets: [{
      label: "Weekly Spending",
      data: weekData,
      borderColor: "black",
      backgroundColor: "yellow",
      tension: 0.1,
    }],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Daily Expenses for the Current Week" },
    },
  };

  return (
    <div className={styles.line}>
      {error && <p>{error}</p>}
      <Line data={chartData} options={chartOptions} />
    </div>
  );
}
