import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import Footer from "../footer/index";
import Cal from"../Calendar/cal";
import Pie from "../piechart/Pie";
import Line from "../Linegraph/Line";
import Navigation from "../NavBar/index";
const Main = () => {
    const apiUrl = "http://localhost:8080";
    useEffect(() => {
        getItems();
        getExItems();
    }, []);
    const [incomes, setIncomes] = useState([]);
    const [error, setError] = useState("");

const getItems = () => {
    fetch(`${apiUrl}/income`, { method: "GET" })
        .then((res) => res.json())
        .then((res) => setIncomes(res))
        .catch(() => setError("Failed to fetch incomes"));
};

const totalIncome = incomes.reduce((sum, item) => sum + item.Amount, 0);

const [expense, setExpense] = useState([]);
useEffect(() => {
        getExItems();
    }, []);
const getExItems = () => {
fetch(`${apiUrl}/expense`, { method: "GET" })
    .then((res) => res.json())
    .then((res) => setExpense(res))
    .catch(() => setError("Failed to fetch expense"));
};

const totalexpense = expense.reduce((sum, item) => sum + item.Amount, 0);
	return (
		<div><Navigation/>
		<div className={styles.main_container}>
			<div className={styles.con}>
			<div className={styles.money}>
				<div className={styles.Total}><h2>Total Income:<sect className={styles.num}>{totalIncome}</sect></h2>
				<h2>Total Expense:<sect className={styles.num2}>{totalexpense}</sect></h2>
				</div><br/>
				<div className={styles.remain}><h2 >Remaining:<sect className={styles.num3}>50000</sect></h2></div>
			</div>
			<br/>
			<div className={styles.piechart}><Pie/></div>
			<br/>
			<div className={styles.cal}><Cal/></div>
			</div>
			<div className={styles.line}><Line/></div>
			<div className={styles.foot}><Footer/></div>
		</div></div>
	);
};

export default Main;
