import { useState, useEffect } from "react";
import styles from "./style.module.css";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Daily() {
  const [source, setSource] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [editId, setEditId] = useState(null);
  const [editSource, setEditSource] = useState("");
  const [editAmount, setEditAmount] = useState("");
  const [editDate, setEditDate] = useState("");

  const apiUrl = "http://localhost:8080/api";

  useEffect(() => {
    fetchExpenses();
  }, []);

  const fetchExpenses = () => {
    fetch(`${apiUrl}/dailyexpense`, { method: "GET" })
      .then((res) => res.json())
      .then((res) => setExpenses(res))
      .catch(() => setError("Failed to fetch expenses"));
  };

  const handleSubmit = () => {
    setError("");

    if (!source.trim() || !amount.trim() || !date.trim()) {
      setError("Source, Amount, and Date are required.");
      return;
    }

    fetch(`${apiUrl}/dailyexpense`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ source, amount: Number(amount), date }),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Expense added successfully");
        setSource("");
        setAmount("");
        setDate("");
        setTimeout(() => setMessage(""), 3000);
        fetchExpenses();
      })
      .catch(() => setError("Unable to create expense entry"));
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this?")) {
      fetch(`${apiUrl}/dailyexpense/${id}`, { method: "DELETE" })
        .then(() => {
          setMessage("Expense deleted successfully");
          setTimeout(() => setMessage(""), 3000);
          fetchExpenses();
        })
        .catch(() => setError("Unable to delete expense entry"));
    }
  };

  const handleEdit = (item) => {
    setEditId(item._id);
    setEditSource(item.source);
    setEditAmount(item.amount);
    setEditDate(item.date);
  };

  const handleUpdate = () => {
    setError("");

    if (!editSource.trim() || !editAmount.trim() || !editDate.trim()) {
      setError("Source, Amount, and Date are required.");
      return;
    }

    fetch(`${apiUrl}/dailyexpense/${editId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ source: editSource, amount: Number(editAmount), date: editDate }),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Expense updated successfully");
        setTimeout(() => setMessage(""), 3000);
        setEditId(null);
        fetchExpenses();
      })
      .catch(() => setError("Unable to update expense entry"));
  };

  const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);

  const chartData = {
    labels: expenses.map((item) => item.date),
    datasets: [
      {
        label: "Daily Expenses",
        data: expenses.map((item) => item.amount),
        borderColor: "#ff6384",
        backgroundColor: "rgba(255, 99, 132, 0.2)",
      },
    ],
  };

  return (
    <div>
      <div className={styles.container}>
        <h3>{editId ? "Edit Daily Expense" : "Add Daily Expense"}</h3>
        {message && <p className="text-success">{message}</p>}
        <div className={styles.formGroup}>
          <input
            placeholder="Expense Source"
            value={editId ? editSource : source}
            onChange={(e) => (editId ? setEditSource(e.target.value) : setSource(e.target.value))}
            className="form-control"
          />
          <input
            type="number"
            placeholder="Amount"
            value={editId ? editAmount : amount}
            onChange={(e) => (editId ? setEditAmount(e.target.value) : setAmount(e.target.value))}
            className="form-control"
          />
          <input
            type="date"
            value={editId ? editDate : date}
            onChange={(e) => (editId ? setEditDate(e.target.value) : setDate(e.target.value))}
            className="form-control"
          />
          {editId ? (
            <>
              <button className="btn btn-success" onClick={handleUpdate}>
                Update
              </button>
              <button className="btn btn-secondary" onClick={() => setEditId(null)}>
                Cancel
              </button>
            </>
          ) : (
            <button className="btn btn-dark" onClick={handleSubmit}>
              Submit
            </button>
          )}
        </div>
        {error && <p className="text-danger">{error}</p>}
      </div>
      <div className={styles.listContainer}>
        <h3>Daily Expenses</h3>
        <ul className="list-group">
          {expenses.map((item) => (
            <li key={item._id} className="list-group-item d-flex justify-content-between">
              <span>
                {item.source} - ${item.amount} ({item.date})
              </span>
              <div>
                <button className="btn btn-warning" onClick={() => handleEdit(item)}>
                  Edit
                </button>
                <button className="btn btn-danger" onClick={() => handleDelete(item._id)}>
                  Delete
                </button>
              </div>
            </li>
          ))}
        </ul>
      </div>
      <h4>Total Daily Expense: ${totalExpense.toFixed(2)}</h4>
    </div>
  );
}
