import React, { useState, useEffect } from "react";
import styles from "./styles.module.css";
import Navigation from "../NavBar";

const BudgetPlan = () => {
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // Form state for creating a new budget plan
  const [name, setName] = useState("");
  const [total, setTotal] = useState("");
  const [spent, setSpent] = useState("");
  const [items, setItems] = useState("");
  const [icon, setIcon] = useState("");

  // Edit form state
  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState("");
  const [editTotal, setEditTotal] = useState("");
  const [editSpent, setEditSpent] = useState("");
  const [editItems, setEditItems] = useState("");
  const [editIcon, setEditIcon] = useState("");

  const apiUrl = "http://localhost:8080/api";

  // Helper to get the auth token from localStorage
  const getAuthHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    };
  };

  useEffect(() => {
    fetchBudgets();
  }, []);

  const fetchBudgets = async () => {
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        headers: getAuthHeaders(),
      });
      if (!res.ok) throw new Error("Failed to fetch budget plans");
      const data = await res.json();
      setBudgets(data);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleSubmit = async () => {
    if (!name || !total) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan`, {
        method: "POST",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name,
          total: Number(total),
          spent: Number(spent) || 0,
          items: Number(items) || 0,
          icon: icon || "ðŸ’°",
        }),
      });
      if (!res.ok) throw new Error("Failed to create budget plan");
      setMessage("Budget plan created successfully!");
      // Clear form fields
      setName("");
      setTotal("");
      setSpent("");
      setItems("");
      setIcon("");
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this budget plan?")) {
      try {
        const res = await fetch(`${apiUrl}/budgetplan/${id}`, {
          method: "DELETE",
          headers: getAuthHeaders(),
        });
        if (!res.ok) throw new Error("Failed to delete budget plan");
        setMessage("Budget plan deleted successfully");
        fetchBudgets();
        setTimeout(() => setMessage(""), 3000);
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleEdit = (plan) => {
    setEditId(plan._id);
    setEditName(plan.name);
    setEditTotal(plan.total);
    setEditSpent(plan.spent);
    setEditItems(plan.items);
    setEditIcon(plan.icon);
  };

  const handleUpdate = async () => {
    if (!editName || !editTotal) {
      setError("Name and Total are required");
      return;
    }
    try {
      const res = await fetch(`${apiUrl}/budgetplan/${editId}`, {
        method: "PUT",
        headers: getAuthHeaders(),
        body: JSON.stringify({
          name: editName,
          total: Number(editTotal),
          spent: Number(editSpent) || 0,
          items: Number(editItems) || 0,
          icon: editIcon || "ðŸ’°",
        }),
      });
      if (!res.ok) throw new Error("Failed to update budget plan");
      setMessage("Budget plan updated successfully");
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
      setEditId(null);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <> <Navigation />
    <div className={styles.container}>


      {/* Create Budget Form */}
      <div className={styles.formContainer}>
        <h1>Budget Plans</h1>
        {message && <div className={styles.success_msg}>{message}</div>}
        {error && <div className={styles.error_msg}>{error}</div>}
        <div className={styles.formGroup}>
          <input
            type="text"
            className={styles.formControl}
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <input
            type="number"
            className={styles.formControl}
            placeholder="Total"
            value={total}
            onChange={(e) => setTotal(e.target.value)}
          />
          <input
            type="number"
            className={styles.formControl}
            placeholder="Spent"
            value={spent}
            onChange={(e) => setSpent(e.target.value)}
          />
          <input
            type="number"
            className={styles.formControl}
            placeholder="Items"
            value={items}
            onChange={(e) => setItems(e.target.value)}
          />
          <input
            type="text"
            className={styles.formControl}
            placeholder="Icon"
            value={icon}
            onChange={(e) => setIcon(e.target.value)}
          />
        </div>
        <div className={styles.buttonGroup}>
          <button type="button" className={styles.btnSuccess} onClick={handleSubmit}>
            Create Plan
          </button>
        </div>
      </div>

      {/* Display Budget Cards */}
      {budgets.length === 0 ? (
        <p>No budget plans found.</p>
      ) : (
        <div className={styles.budgetGrid}>
          {budgets.map((plan) => (
            <div key={plan._id} className={styles.budgetCard}>
              <div className={styles.budgetHeader}>
                <span className={styles.icon}>{plan.icon}</span>
                <span className={styles.budgetTitle}>{plan.name}</span>
                <span className={styles.amount}>${plan.total}</span>
              </div>
              <p className={styles.budgetInfo}>Spent: ${plan.spent}</p>
              <p className={styles.budgetInfo}>Items: {plan.items}</p>
              <div className={styles.cardButtons}>
                <button className={styles.btnWarning} onClick={() => handleEdit(plan)}>
                  Edit
                </button>
                <button className={styles.btnDanger} onClick={() => handleDelete(plan._id)}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Edit Budget Form */}
      {editId && (
        <div className={styles.formContainer}>
          <h2>Edit Budget Plan</h2>
          <div className={styles.formGroup}>
            <input
              type="text"
              className={styles.formControl}
              placeholder="Name"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
            />
            <input
              type="number"
              className={styles.formControl}
              placeholder="Total"
              value={editTotal}
              onChange={(e) => setEditTotal(e.target.value)}
            />
            <input
              type="number"
              className={styles.formControl}
              placeholder="Spent"
              value={editSpent}
              onChange={(e) => setEditSpent(e.target.value)}
            />
            <input
              type="number"
              className={styles.formControl}
              placeholder="Items"
              value={editItems}
              onChange={(e) => setEditItems(e.target.value)}
            />
            <input
              type="text"
              className={styles.formControl}
              placeholder="Icon"
              value={editIcon}
              onChange={(e) => setEditIcon(e.target.value)}
            />
          </div>
          <div className={styles.buttonGroup}>
            <button type="button" className={styles.btnSuccess} onClick={handleUpdate}>
              Save
            </button>
            <button
              type="button"
              className={styles.btnSecondary}
              onClick={() => setEditId(null)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div></>
  );
};

export default BudgetPlan;
