import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import Footer from "../footer/index";
import Cal from "../Calendar/cal";
import Pie from "../piechart/Pie";
import Line from "../Linegraph/Line";
import Navigation from "../NavBar/index";
import Bar from "../Bargraph/bargraph";


const Main = () => {
    const [incomes, setIncomes] = useState([]);
  const [error, setError] = useState("");
  const [expenses, setExpenses] = useState([]);
    const apiUrl = "http://localhost:8080";

// Helper to get the auth headers from localStorage
const getAuthHeaders = () => {
  const token = localStorage.getItem("token");
  return {
    "Content-Type": "application/json",
    "Authorization": `Bearer ${token}`,
  };
};

  useEffect(() => {
    getItems();
    fetchExpenses()
  }, []);
  const apiUr = "http://localhost:8080/api/expense";

  // Helper to get auth headers (JWT from localStorage)
  const getAutHeaders = () => {
    const token = localStorage.getItem("token");
    return {
      "Content-Type": "application/json",
      "Authorization": `Bearer ${token}`,
    };
  };

  // Fetch all expenses and graph data when component mounts


  // Fetch expenses from backend with auth headers
  const fetchExpenses = () => {
    fetch(apiUr, { method: "GET", headers: getAutHeaders() })
      .then((res) => res.json())
      .then((data) => setExpenses(data))
      .catch(() => setError("Failed to fetch expenses"));
  };


  const getItems = () => {
    fetch(`${apiUrl}/income`, { method: "GET", headers: getAuthHeaders() })
      .then((res) => res.json())
      .then((res) => setIncomes(res))
      .catch(() => setError("Failed to fetch incomes"));
  };
  const totalIncome=incomes.reduce((sum, item) => sum + item.Amount, 0);
  const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);

    const remainingBalance = totalIncome - totalExpense; 

    return (
        <div>
            <Navigation />
            <div className={styles.main_container}>
                <div className={styles.con}>
                    <div className={styles.money}>
                        <div className={styles.Total}>
                            <h2>Total Income: <span className={styles.num}>${totalIncome}</span></h2>
                            <h2>Total Expense: <span className={styles.num2}>${totalExpense}</span></h2>
                        </div>
                        <br />
                        <div className={styles.remain}>
                            <h2>Remaining: <span className={styles.num3}>${remainingBalance}</span></h2>
                        </div>
                    </div>
                    {/* <br />
                    <div className={styles.piechart}><Pie /></div>
                    <br />
                    <div className={styles.cal}><Cal /></div>
                <div className={styles.line}><Line /></div>
                <div className={styles.line}><Bar /></div>
                <div className={styles.foot}><Footer /></div> */}
                            </div>
                            </div>
        </div>
    );
};

export default Main;
