import { useEffect, useState } from "react";
import Navigation from "../NavBar";
import "./in.css";

export default function Expense() {
    const [expense, setExpense] = useState("");
    const [amount, setAmount] = useState("");
    const [reference, setReference] = useState("");
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const [editId, setEditId] = useState(null);
    const [editExpense, setEditExpense] = useState("");
    const [editAmount, setEditAmount] = useState("");
    const [editReference, setEditReference] = useState("");

    const apiUrl = "http://localhost:8080/api"; // Ensure API matches backend

    useEffect(() => {
        getItems();
    }, []);

    const getItems = () => {
        fetch(`${apiUrl}/expense`)
            .then((res) => res.json())
            .then((res) => setExpenses(res))
            .catch(() => setError("Failed to fetch Expenses"));
    };

    const handleSubmit = () => {
        setError("");

        if (!expense.trim() || !amount.trim()) {
            setError("Expense Source and Amount are required.");
            return;
        }

        fetch(`${apiUrl}/expense`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ expense, amount: Number(amount), reference }),
        })
            .then((res) => res.json())
            .then(() => {
                setMessage("Expense added successfully");
                setExpense("");
                setAmount("");
                setReference("");
                setTimeout(() => setMessage(""), 3000);
                getItems(); // Refresh list
            })
            .catch(() => setError("Unable to create Expense entry"));
    };

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this?")) {
            fetch(`${apiUrl}/expense/${id}`, { method: "DELETE" })
                .then(() => {
                    setMessage("Expense deleted successfully");
                    setTimeout(() => setMessage(""), 3000);
                    getItems(); // Refresh list
                })
                .catch(() => setError("Unable to delete Expense entry"));
        }
    };

    const handleEdit = (item) => {
        setEditId(item._id);
        setEditExpense(item.expense);
        setEditAmount(item.amount);
        setEditReference(item.reference);
    };

    const handleUpdate = () => {
        setError("");

        if (!editExpense.trim() || !editAmount.trim()) {
            setError("Expense Source and Amount are required.");
            return;
        }

        fetch(`${apiUrl}/expense/${editId}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ expense: editExpense, amount: Number(editAmount), reference: editReference }),
        })
            .then((res) => res.json())
            .then(() => {
                setMessage("Expense updated successfully");
                setTimeout(() => setMessage(""), 3000);
                setEditId(null);
                getItems(); // Refresh list
            })
            .catch(() => setError("Unable to update Expense entry"));
    };

    const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);

    return (
        <div>
            <Navigation />
            <div className="container">
                <div className="form-container">
                    <h3>{editId ? "Edit Expense" : "Add Expense"}</h3>
                    {message && <p className="text-success">{message}</p>}
                    <div className="form-group">
                        <input
                            placeholder="Expense Source"
                            value={editId ? editExpense : expense}
                            onChange={(e) => (editId ? setEditExpense(e.target.value) : setExpense(e.target.value))}
                            className="form-control"
                        />
                        <input
                            type="number"
                            placeholder="Amount"
                            value={editId ? editAmount : amount}
                            onChange={(e) => (editId ? setEditAmount(e.target.value) : setAmount(e.target.value))}
                            className="form-control"
                        />
                        <input
                            placeholder="Reference (optional)"
                            value={editId ? editReference : reference}
                            onChange={(e) => (editId ? setEditReference(e.target.value) : setReference(e.target.value))}
                            className="form-control"
                        />
                        {editId ? (
                            <>
                                <button onClick={handleUpdate}>Update</button>
                                <button onClick={() => setEditId(null)}>Cancel</button>
                            </>
                        ) : (
                            <button onClick={handleSubmit}>Submit</button>
                        )}
                    </div>
                </div>

                <div className="total-Expense">
                    <h4>Total Expense: ${totalExpense.toFixed(2)}</h4>
                </div>
            </div>
        </div>
    );
}
