import { useEffect, useState } from "react";
import styles from "./styles.module.css";
import Footer from "../footer/index";
import Cal from "../Calendar/cal";
import Pie from "../piechart/Pie";
import Line from "../Linegraph/Line";
import Navigation from "../NavBar/index";
import Bar from "../Bargraph/bargraph";

const Main = () => {
    const apiUrl = "http://localhost:8080";
    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        getItems();
        fetch(`${apiUrl}/api/expense`)
            .then((res) => res.json())
            .then((data) => setExpenses(data))
            .catch(() => setError("Failed to fetch expenses"));
    }, []);

    const getItems = () => {
        fetch(`${apiUrl}/income`)
            .then((res) => res.json())
            .then((data) => setIncomes(data))
            .catch(() => setError("Failed to fetch incomes"));
    };

    const totalExpense = expenses.reduce((sum, expense) => sum + (expense.amount || 0), 0);
    const totalIncome = incomes.reduce((sum, item) => sum + (item.amount || 0), 0);
    const remainingBalance = totalIncome - totalExpense; 

    return (
        <div>
            <Navigation />
            <div className={styles.main_container}>
                <div className={styles.con}>
                    <div className={styles.money}>
                        <div className={styles.Total}>
                            <h2>Total Income: <span className={styles.num}>${totalIncome}</span></h2>
                            <h2>Total Expense: <span className={styles.num2}>${totalExpense}</span></h2>
                        </div>
                        <br />
                        <div className={styles.remain}>
                            <h2>Remaining: <span className={styles.num3}>${remainingBalance}</span></h2>
                        </div>
                    </div>
                    <br />
                    <div className={styles.piechart}><Pie /></div>
                    <br />
                    <div className={styles.cal}><Cal /></div>
                </div>
                <div className={styles.line}><Line /></div>
                <div className={styles.line}><Bar /></div>
                <div className={styles.foot}><Footer /></div>
            </div>
        </div>
    );
};

export default Main;
