import { NavLink } from "react-router-dom";
import styles from "./styles.module.css";
import img1 from "../Images/home.jpg"
import Footer from "../footer/index";
const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				{/* Left Section with Logo */}
				<h1 className={styles.nav_logo}>LEARNZ</h1>

				{/* Right Section with Navigation Links and Logout Button */}
				<div className={styles.nav_links_container}>
					<div className={styles.nav_links}>
						{/* Home Link */}
						<NavLink
							to="/"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Home
						</NavLink>

						{/* Courses Link */}
						<NavLink
							to="/courses"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Courses
						</NavLink>

						{/* Contact Link */}
						<NavLink
							to="/contact"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Contact
						</NavLink>

						{/* About Link */}
						<NavLink
							to="/about"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							About
						</NavLink>
					</div>

					{/* Logout Button */}
                    <div className="login-bar"><p>|</p>
                    </div>
					<button className={styles.white_btn} onClick={handleLogout}>
                    Logout
					</button>
				</div>
			</nav>

			<div className={styles.content}>
			<img 
        src={img1}
        style={{ width: '1522px',height:'600px',opacity:0.6}} 
      />

			</div>
			<Footer/>
		</div>
	);
};

export default Main;
