import React from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

// Register chart components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

/**
 * WeeklyExpenseChart component
 * @param {Array} expenses - Array of expense objects with at least:
 *   - date: A date string (ISO format or similar) e.g. "2025-01-20T00:00:00.000Z"
 *   - amount: A number representing the expense amount
 */
const WeeklyExpenseChart = ({ expenses = [] }) => {
  // Get ISO date strings for the current week's Monday to Sunday
  const getCurrentWeekDates = () => {
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Sun) ... 6 (Sat)
    // Calculate Monday: if today is Sunday (0), ((0 + 6) % 7) = 6 days back.
    const monday = new Date(today);
    monday.setDate(today.getDate() - ((dayOfWeek + 6) % 7));

    const weekDates = [];
    for (let i = 0; i < 7; i++) {
      const currentDate = new Date(monday);
      currentDate.setDate(monday.getDate() + i);
      weekDates.push(currentDate.toISOString().split("T")[0]);
    }
    return weekDates;
  };

  // Compute labels and data for the current week
  const getWeekData = () => {
    const weekDates = getCurrentWeekDates();

    // Sum expenses for each day of the week
    const weekData = weekDates.map((dateStr) => {
      return expenses.reduce((sum, expense) => {
        // Assume expense.date is an ISO string (or similar) and compare only the date portion.
        const expenseDate = expense.date.split("T")[0];
        return expenseDate === dateStr ? sum + expense.amount : sum;
      }, 0);
    });

    // Format labels as "Day(dd|mm|yyyy)"
    const dayNames = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
    const weekLabels = weekDates.map((dateStr, index) => {
      const d = new Date(dateStr);
      const day = d.getDate();
      const month = String(d.getMonth() + 1).padStart(2, "0");
      const year = d.getFullYear();
      return `${dayNames[index]}(${day}|${month}|${year})`;
    });

    return { weekLabels, weekData };
  };

  const { weekLabels, weekData } = getWeekData();

  // Prepare the chart data
  const chartData = {
    labels: weekLabels,
    datasets: [
      {
        label: "Weekly Spending",
        data: weekData,
        borderColor: "rgb(75,192,197)",
        backgroundColor: "rgba(75,192,197,0.2)",
        tension: 0.4,
      },
    ],
  };

  return (
    <div>
      <Line data={chartData} />
    </div>
  );
};

export default WeeklyExpenseChart;
