import { NavLink } from "react-router-dom";
import styles from "./styles.module.css";

const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				{/* Left Section with Navigation Links */}
				<div className={styles.nav_links_container}>
					<div className={styles.nav_links}>
						{/* Home Link */}
						<NavLink
							to="/"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Home
						</NavLink>

						{/* Courses Link */}
						<NavLink
							to="/courses"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Courses
						</NavLink>

						{/* Contact Link */}
						<NavLink
							to="/contact"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							Contact
						</NavLink>

						{/* About Link */}
						<NavLink
							to="/about"
							className={({ isActive }) =>
								isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
							}
						>
							About
						</NavLink>
					</div>
				</div>

				{/* Right Section with Logo and Logout Button */}
				<div className={styles.nav_right}>
					<h1>Learnz</h1>
					<button className={styles.white_btn} onClick={handleLogout}>
						Logout
					</button>
				</div>
			</nav>

			<div className={styles.content}>
				<h2>Welcome to Learnz!</h2>
				<p>This is your personalized learning platform.</p>
			</div>
		</div>
	);
};

export default Main;
