import { useState, useEffect } from "react"; 
import styles from "./style.module.css";
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Daily() {
  const [source, setSource] = useState("");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // Update this URL based on your backend configuration
  const apiUrl = "http://localhost:8080/api"; 

  // Fetch expenses on mount
  useEffect(() => {
    fetchExpenses();
  }, []);

  // Fetch expenses from backend and exclude monthly expenses if any
  const fetchExpenses = async () => {
    try {
      const res = await fetch(`${apiUrl}/dailyexpense`);
      if (!res.ok) {
        throw new Error("Failed to fetch expenses");
      }
      const data = await res.json();
      // Exclude any expense with expenseType === "Monthly" (if available)
      const dailyExpenses = data.filter(expense => 
        !expense.expenseType || expense.expenseType === "Daily"
      );
      setExpenses(dailyExpenses);
    } catch (err) {
      setError(err.message || "Failed to fetch expenses");
    }
  };

  // Handle expense addition
  const handleSubmit = async () => {
    setError("");

    if (!source || !amount || !date) {
      setError("All fields are required");
      return;
    }
    if (isNaN(amount) || amount <= 0) {
      setError("Amount should be a positive number");
      return;
    }

    try {
      const res = await fetch(`${apiUrl}/dailyexpense`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ source, amount: Number(amount), date }),
      });

      if (res.ok) {
        setMessage("Expense added successfully!");
        setSource("");
        setAmount("");
        setDate("");
        fetchExpenses();
        setTimeout(() => setMessage(""), 3000);
      } else {
        throw new Error("Unable to add expense");
      }
    } catch (err) {
      setError(err.message || "Unable to add expense");
    }
  };

  // This function computes the current week's Monday and returns an array of 7 ISO date strings (Monday to Sunday)
  const getCurrentWeekDates = () => {
    const today = new Date();
    const dayOfWeek = today.getDay(); // 0 (Sun) ... 6 (Sat)
    // Calculate Monday (if today is Sunday, go back 6 days)
    const monday = new Date(today);
    monday.setDate(today.getDate() - ((dayOfWeek + 6) % 7));

    const weekDates = [];
    for (let i = 0; i < 7; i++) {
      const currentDate = new Date(monday);
      currentDate.setDate(monday.getDate() + i);
      // Get ISO string in the format yyyy-mm-dd
      weekDates.push(currentDate.toISOString().split("T")[0]);
    }
    return weekDates;
  };

  // Generate week labels and data for expenses
  const getWeekData = () => {
    const weekDates = getCurrentWeekDates();

    // Sum expenses for each day of the week.
    const weekData = weekDates.map(dateStr => {
      return expenses.reduce((sum, expense) => {
        // Extract the date portion from expense.date
        const expenseDate = expense.date.split("T")[0];
        return expenseDate === dateStr ? sum + expense.amount : sum;
      }, 0);
    });

    // Format labels as "Day(dd|mm|yyyy)"
    const dayNames = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
    const weekLabels = weekDates.map((dateStr, index) => {
      const d = new Date(dateStr);
      const day = d.getDate();
      const month = String(d.getMonth() + 1).padStart(2, '0');
      const year = d.getFullYear();
      return `${dayNames[index]}(${day}|${month}|${year})`;
    });

    return { weekLabels, weekData };
  };

  const { weekLabels, weekData } = getWeekData();

  const chartData = {
    labels: weekLabels,
    datasets: [{
      label: "Weekly Spending",
      data: weekData,
      borderColor: "rgb(75,192,197)",
      backgroundColor: "rgba(75,192,197,0.2)",
      tension: 0.4,
    }],
  };

  return (
    <div className={styles.line}>
      <Line data={chartData} />
    </div>
  );
}
