import { useEffect, useState } from "react";
import Navigation from "../NavBar";
import styles from "./style.module.css";

export default function CombinedExpense() {
  const [expenseType, setExpenseType] = useState("Monthly");
  const [expense, setExpense] = useState("Rent");
  const [amount, setAmount] = useState("");
  const [reference, setReference] = useState("");
  const [date, setDate] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [editId, setEditId] = useState(null);
  const [editExpenseType, setEditExpenseType] = useState("Monthly");
  const [editExpense, setEditExpense] = useState("Rent");
  const [editAmount, setEditAmount] = useState("");
  const [editReference, setEditReference] = useState("");
  const [editDate, setEditDate] = useState("");

  // This endpoint should handle both monthly and daily expense entries
  const apiUrl = "http://localhost:8080/api/expense";

  useEffect(() => {
    getItems();
  }, []);

  const getItems = () => {
    fetch(apiUrl, { method: "GET" })
      .then((res) => res.json())
      .then((data) => setExpenses(data))
      .catch(() => setError("Failed to fetch expenses"));
  };

  const handleSubmit = () => {
    setError("");
    if (!expense.trim() || !amount.trim() || !date.trim()) {
      setError("Expense, Amount, and Date are required.");
      return;
    }

    const payload = {
      expenseType,
      expense,
      amount: Number(amount),
      reference,
      date,
    };

    fetch(apiUrl, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Expense added successfully");
        setExpenseType("Monthly");
        setExpense("Rent");
        setAmount("");
        setReference("");
        setDate("");
        setTimeout(() => setMessage(""), 3000);
        getItems();
      })
      .catch(() => setError("Unable to create expense entry"));
  };

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this?")) {
      fetch(`${apiUrl}/${id}`, { method: "DELETE" })
        .then(() => {
          setMessage("Expense deleted successfully");
          setTimeout(() => setMessage(""), 3000);
          getItems();
        })
        .catch(() => setError("Unable to delete expense entry"));
    }
  };

  const handleEdit = (item) => {
    setEditId(item._id);
    setEditExpenseType(item.expenseType);
    setEditExpense(item.expense);
    setEditAmount(item.amount);
    setEditReference(item.reference);
    setEditDate(item.date);
  };

  const handleUpdate = () => {
    setError("");
    if (!editExpense.trim() || !editAmount.toString().trim() || !editDate.trim()) {
      setError("Expense, Amount, and Date are required.");
      return;
    }
    const payload = {
      expenseType: editExpenseType,
      expense: editExpense,
      amount: Number(editAmount),
      reference: editReference,
      date: editDate,
    };

    fetch(`${apiUrl}/${editId}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Expense updated successfully");
        setTimeout(() => setMessage(""), 3000);
        setEditId(null);
        getItems();
      })
      .catch(() => setError("Unable to update expense entry"));
  };

  const totalExpense = expenses.reduce((sum, item) => sum + item.amount, 0);

  return (
    <div>
      <Navigation />
      <div className={styles.container}>
        <div className={styles.formContainer}>
          <h3>{editId ? "Edit Expense" : "Add Expense"}</h3>
          {message && <div className={styles.success_msg}>{message}</div>}
          <div className={styles.formGroup}>
            {editId ? (
              <select
                value={editExpenseType}
                onChange={(e) => setEditExpenseType(e.target.value)}
                className={styles.formControl}
              >
                <option value="Monthly">Monthly</option>
                <option value="Daily">Daily</option>
              </select>
            ) : (
              <select
                value={expenseType}
                onChange={(e) => setExpenseType(e.target.value)}
                className={styles.formControl}
              >
                <option value="Monthly">Monthly</option>
                <option value="Daily">Daily</option>
              </select>
            )}

            {editId ? (
              <input
                placeholder="Expense Name"
                value={editExpense}
                onChange={(e) => setEditExpense(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                placeholder="Expense Name"
                value={expense}
                onChange={(e) => setExpense(e.target.value)}
                className={styles.formControl}
              />
            )}
            {editId ? (
              <input
                type="number"
                placeholder="Amount"
                value={editAmount}
                onChange={(e) => setEditAmount(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="number"
                placeholder="Amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                className={styles.formControl}
              />
            )}
            {editId ? (
              <input
                placeholder="Reference (optional)"
                value={editReference}
                onChange={(e) => setEditReference(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                placeholder="Reference (optional)"
                value={reference}
                onChange={(e) => setReference(e.target.value)}
                className={styles.formControl}
              />
            )}
            {editId ? (
              <input
                type="date"
                value={editDate}
                onChange={(e) => setEditDate(e.target.value)}
                className={styles.formControl}
              />
            ) : (
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className={styles.formControl}
              />
            )}

            {editId ? (
              <div className={styles.buttonGroup}>
                <button className={styles.btnSuccess} onClick={handleUpdate}>
                  Update
                </button>
                <button className={styles.btnSecondary} onClick={() => setEditId(null)}>
                  Cancel
                </button>
              </div>
            ) : (
              <button className={styles.btnDark} onClick={handleSubmit}>
                Submit
              </button>
            )}
          </div>
          {error && <div className={styles.error_msg}>{error}</div>}
        </div>

        <div className={styles.tableContainer}>
          <h3>Expense List</h3>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>Type</th>
                <th>Expense Source</th>
                <th>Amount</th>
                <th>Reference</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((item) => (
                <tr key={item._id}>
                  <td>{item.expenseType}</td>
                  <td>{item.expense}</td>
                  <td>${item.amount}</td>
                  <td>{item.reference || "N/A"}</td>
                  <td>{item.date}</td>
                  <td>
                    <button className={styles.btnWarning} onClick={() => handleEdit(item)}>
                      Edit
                    </button>
                    <button className={styles.btnDanger} onClick={() => handleDelete(item._id)}>
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className={styles.totalExpense}>
          <h4>Total Expense: ${totalExpense.toFixed(2)}</h4>
        </div>
      </div>
    </div>
  );
}
