import { useState, useEffect } from "react";
import styles from "./styles.module.css";
import Footer from "../footer/index";
import Cal from "../Calendar/cal";
import Pie from "../piechart/Pie";
import Line from "../Linegraph/Line";
import Navigation from "../NavBar/index";

const Main = () => {
  const apiUrl = "http://localhost:8080";
  const [incomes, setIncomes] = useState([]);
  const [monthlyExpenses, setMonthlyExpenses] = useState([]);
  const [dailyExpenses, setDailyExpenses] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    getIncomes();
    getMonthlyExpenses();
    getDailyExpenses();
  }, []);

  const getIncomes = () => {
    fetch(`${apiUrl}/income`)
      .then((res) => res.json())
      .then((data) => setIncomes(data))
      .catch(() => setError("Failed to fetch incomes"));
  };

  const getMonthlyExpenses = () => {
    fetch(`${apiUrl}/expense`)
      .then((res) => res.json())
      .then((data) => setMonthlyExpenses(data))
      .catch(() => setError("Failed to fetch monthly expenses"));
  };

  const getDailyExpenses = () => {
    fetch(`${apiUrl}/dailyexpense`)
      .then((res) => res.json())
      .then((data) => setDailyExpenses(data))
      .catch(() => setError("Failed to fetch daily expenses"));
  };

  // Compute totals:
  // Total income from incomes array (assuming property "Amount" exists)
  const totalIncome = incomes.reduce((sum, item) => sum + item.Amount, 0);
  // Total monthly expense from monthlyExpenses array (assuming "amount" property)
  const totalMonthlyExpense = monthlyExpenses.reduce(
    (sum, item) => sum + (item.amount || 0),
    0
  );
  // Total daily expense from dailyExpenses array (assuming "amount" property)
  const totalDailyExpense = dailyExpenses.reduce(
    (sum, item) => sum + (item.amount || 0),
    0
  );
  // Combine monthly and daily expenses to get overall expense
  const totalExpense = totalMonthlyExpense + totalDailyExpense;
  const remainingBalance = totalIncome - totalExpense;

  return (
    <div>
      <Navigation />
      <div className={styles.main_container}>
        <div className={styles.con}>
          <div className={styles.money}>
            <div className={styles.Total}>
              <h2>
                Total Income: <span className={styles.num}>${totalIncome}</span>
              </h2>
              <h2>
                Total Expense:{" "}
                <span className={styles.num2}>${totalExpense}</span>
              </h2>
            </div>
            <br />
            <div className={styles.remain}>
              <h2>
                Remaining:{" "}
                <span className={styles.num3}>${remainingBalance}</span>
              </h2>
            </div>
          </div>
          <br />
          <div className={styles.piechart}>
            <Pie />
          </div>
          <br />
          <div className={styles.cal}>
            <Cal />
          </div>
        </div>
        <div className={styles.line}>
          <Line />
        </div>
        <div className={styles.foot}>
          <Footer />
        </div>
      </div>
    </div>
  );
};

export default Main;
