import { useEffect, useState } from "react";
import Navigation from "../NavBar";
import "./in.css";

export default function Income() {
    const [IncomeSource, setIncomeSource] = useState("");
    const [Amount, setAmount] = useState("");
    const [reference, setReference] = useState("");
    const [incomes, setIncomes] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const [editId, setEditId] = useState(null);
    const [editIncomeSource, setEditIncomeSource] = useState("");
    const [editAmount, setEditAmount] = useState("");
    const [editReference, setEditReference] = useState("");

    const apiUrl = "http://localhost:8080";

    const handleSubmit = () => {
        setError("");
        if (IncomeSource.trim() !== "" && Amount.trim() !== "") {
            fetch(apiUrl + "/incomeAdd", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ IncomeSource, Amount, reference })
            })
                .then((res) => res.json())
                .then((newIncome) => {
                    setIncomes([...incomes, newIncome]);
                    setMessage("Income added successfully");
                    setIncomeSource("");
                    setAmount("");
                    setReference("");
                    setTimeout(() => setMessage(""), 3000);
                })
                .catch(() => setError("Unable to create income entry"));
        }
    };

    useEffect(() => {
        getItems();
    }, []);

    const getItems = () => {
        fetch(apiUrl + "/incomeAdd", { method: "GET" })
            .then((res) => res.json())
            .then((res) => setIncomes(res));
    };

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this?")) {
            fetch(apiUrl + "/incomeAdd/" + id, { method: "DELETE" })
                .then(() => {
                    setIncomes(incomes.filter((item) => item._id !== id));
                })
                .catch(() => setError("Unable to delete income entry"));
        }
    };

    const handleEdit = (item) => {
        setEditId(item._id);
        setEditIncomeSource(item.IncomeSource);
        setEditAmount(item.Amount);
        setEditReference(item.reference);
    };

    const handleUpdate = () => {
        setError("");
        fetch(apiUrl + "/incomeAdd/" + editId, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ IncomeSource: editIncomeSource, Amount: editAmount, reference: editReference })
        })
            .then((res) => res.json())
            .then((updatedIncome) => {
                setIncomes(incomes.map((item) => (item._id === editId ? updatedIncome : item)));
                setMessage("Income updated successfully");
                setTimeout(() => setMessage(""), 3000);
                setEditId(null);
            })
            .catch(() => setError("Unable to update income entry"));
    };

    return (
        <div>
            <Navigation />
            <div className="row p-3 bg-success text-light">
                <h3>Add Income</h3>
                {message && <p className="text-success">{message}</p>}
                <div className="form-group">
                    <input placeholder="Income Source" value={IncomeSource} onChange={(e) => setIncomeSource(e.target.value)} className="form-control" />
                    <input placeholder="Amount" value={Amount} onChange={(e) => setAmount(e.target.value)} className="form-control" />
                    <input placeholder="Reference (optional)" value={reference} onChange={(e) => setReference(e.target.value)} className="form-control" />
                    <button className="btn btn-dark" onClick={handleSubmit}>Submit</button>
                </div>
                {error && <p className="text-danger">{error}</p>}
            </div>

            <h3>Income List</h3>
            <ul className="list-group">
                {incomes.map((item) => (
                    <li key={item._id} className="list-group-item d-flex justify-content-between">
                        <span>{item.IncomeSource} - {item.Amount}</span>
                        <div>
                            <button className="btn btn-warning" onClick={() => handleEdit(item)}>Edit</button>
                            <button className="btn btn-danger" onClick={() => handleDelete(item._id)}>Delete</button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}
