import { useState, useEffect } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS } from "chart.js";
import { BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from "chart.js";

// Register the necessary Chart.js components
ChartJS.register(BarElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

export default function BudgetChart() {
    const [budgets, setBudgets] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const apiUrl = "http://localhost:8080/api"; // Update with your backend URL

    useEffect(() => {
        fetchBudgetAndExpenses();
    }, []);

    const fetchBudgetAndExpenses = async () => {
        try {
            const budgetRes = await fetch(`${apiUrl}/budget`);
            const expenseRes = await fetch(`${apiUrl}/dailyexpense`);

            if (!budgetRes.ok || !expenseRes.ok) {
                throw new Error("Failed to fetch data");
            }

            const budgetData = await budgetRes.json();
            const expenseData = await expenseRes.json();

            setBudgets(budgetData);
            setExpenses(expenseData);
        } catch (err) {
            console.error("Error fetching data:", err);
        }
    };

    const budgetMap = budgets.reduce((acc, item) => {
        acc[item.budgetName] = item.amount;
        return acc;
    }, {});

    const expenseMap = expenses.reduce((acc, item) => {
        acc[item.source] = (acc[item.source] || 0) + item.amount;
        return acc;
    }, {});

    const labels = Array.from(new Set([...Object.keys(budgetMap), ...Object.keys(expenseMap)]));
    const budgetData = labels.map(label => budgetMap[label] || 0);
    const expenseData = labels.map(label => expenseMap[label] || 0);

    const chartData = {
        labels,
        datasets: [
            {
                label: "Budget",
                data: budgetData,
                backgroundColor: "#ffeb99", // Light yellow color for budget
                borderColor: "#ffcc00",     // Dark yellow for border
                borderWidth: 1,
            },
            {
                label: "Spending",
                data: expenseData,
                backgroundColor: "#ffcc00", // Dark yellow color for spending
                borderColor: "#e6a800",     // Even darker yellow for border
                borderWidth: 1,
            },
        ],
    };

    return (
        <div className="container">
            <h3>Budget vs Spending</h3>
            <Bar data={chartData} />
        </div>
    );
}
