import { NavLink, useNavigate } from "react-router-dom";
import styles from "./styles.module.css";

const Navigation = () => {
  const navigate = useNavigate();

  // Handle logout process
  const handleLogout = async () => {
    try {
      // Make a request to the backend to handle logout and clear session on the server side
      const response = await fetch("http://localhost:8080/logout", {
        method: "POST",
        credentials: "include", // Ensures cookies are sent with the request
      });

      if (response.ok) {
        // Clear the token from localStorage
        localStorage.removeItem("token");

        // Redirect the user to the login page
        navigate("/login");
      } else {
        throw new Error("Logout failed, server error");
      }
    } catch (error) {
      console.error("Logout failed", error);
      alert("Logout failed! Please try again.");
    }
  };

  return (
    <div className={styles.nav_links_container}>
      <nav className={styles.navbar}>
        <h1 className={styles.nav_logo}>WealthWise</h1>
        <div className={styles.nav_links_container}>
          <div className={styles.nav_links}>
            <NavLink
              to="/"
              className={({ isActive }) =>
                isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
              }
            >
              DashBoard
            </NavLink>
            <NavLink
              to="/Income"
              className={({ isActive }) =>
                isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
              }
            >
              Income
            </NavLink>
            <NavLink
              to="/Expenses"
              className={({ isActive }) =>
                isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
              }
            >
              Expenses
            </NavLink>
            <NavLink
              to="/Budgetplan"
              className={({ isActive }) =>
                isActive ? `${styles.nav_link} ${styles.active}` : styles.nav_link
              }
            >
              Budgetplan
            </NavLink>
          </div>
          <div className="login-bar">
            <p className={styles.logoutBar}>|</p>
          </div>
          <button className={styles.white_btn} onClick={handleLogout}>
            Logout
          </button>
        </div>
      </nav>
    </div>
  );
};

export default Navigation;
