import React, { useState, useEffect } from "react";
import styles from "./styles.module.css";
import Navigation from "../NavBar";
import axios from "axios"; // Import axios

const BudgetPlan = () => {
  const [budgets, setBudgets] = useState([]);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // ... (Form and Editing state - no changes needed)

  const apiUrl = "http://localhost:8080/api";

  useEffect(() => {
    fetchBudgets();
  }, []);

  const fetchBudgets = async () => {
    try {
      const token = localStorage.getItem('token'); // Get token from local storage

      const res = await axios.get(`${apiUrl}/budgetplan`, {
        headers: {
          'Authorization': `Bearer ${token}` // Send token in header
        }
      });

      setBudgets(res.data);
    } catch (err) {
      setError(err.response?.data?.message || err.message); // Improved error handling
    }
  };


  const handleSubmit = async () => {
    // ... (Validation - no changes needed)
    try {
      const token = localStorage.getItem('token');

      const res = await axios.post(`${apiUrl}/budgetplan`, {
        name,
        total: Number(total),
        spent: Number(spent) || 0,
        items: Number(items) || 0,
        icon: icon || "ðŸ’°",
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setMessage("Budget plan created successfully!");
      // ... (Clear form fields)
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setError(err.response?.data?.message || err.message); // Improved error handling
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this budget plan?")) {
      try {
        const token = localStorage.getItem('token');

        const res = await axios.delete(`${apiUrl}/budgetplan/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        setMessage("Budget plan deleted successfully");
        fetchBudgets();
        setTimeout(() => setMessage(""), 3000);
      } catch (err) {
        setError(err.response?.data?.message || err.message); // Improved error handling
      }
    }
  };

  const handleUpdate = async () => {
    // ... (Validation - no changes needed)
    try {
      const token = localStorage.getItem('token');

      const res = await axios.put(`${apiUrl}/budgetplan/${editId}`, {
        name: editName,
        total: Number(editTotal),
        spent: Number(editSpent) || 0,
        items: Number(editItems) || 0,
        icon: editIcon || "ðŸ’°",
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      setMessage("Budget plan updated successfully");
      setEditId(null);
      fetchBudgets();
      setTimeout(() => setMessage(""), 3000);
    } catch (err) {
      setError(err.response?.data?.message || err.message); // Improved error handling
    }
  };

  // ... (rest of the component - no changes needed)

};

export default BudgetPlan;
