import { useState, useEffect } from "react";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
import "./in.css";

export default function ExpenseTracker() {
    const [source, setSource] = useState("");
    const [amount, setAmount] = useState("");
    const [date, setDate] = useState("");
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");

    const apiUrl = "http://localhost:8080";

    // Fetching expenses when the component mounts
    useEffect(() => {
        fetchExpenses();
    }, []);

    // Function to fetch expenses from the backend
    const fetchExpenses = async () => {
        try {
            const res = await fetch(`${apiUrl}/expenses`);
            if (!res.ok) {
                throw new Error("Failed to fetch expenses");
            }
            const data = await res.json();
            setExpenses(data);
        } catch (err) {
            setError(err.message || "Failed to fetch expenses");
        }
    };

    // Function to handle adding an expense
    const handleSubmit = async () => {
        setError(""); // Reset any previous errors
        // Validate inputs
        if (!source || !amount || !date) {
            setError("All fields are required");
            return;
        }
        if (isNaN(amount) || amount <= 0) {
            setError("Amount should be a positive number");
            return;
        }

        try {
            const res = await fetch(`${apiUrl}/expense`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ source, amount: Number(amount), date }),
            });

            if (res.ok) {
                setMessage("Expense added successfully!");
                setSource(""); // Clear the input fields
                setAmount("");
                setDate("");
                fetchExpenses(); // Refresh the expense list
                setTimeout(() => setMessage(""), 3000); // Clear the success message after 3 seconds
            } else {
                throw new Error("Unable to add expense");
            }
        } catch (err) {
            setError(err.message || "Unable to add expense");
        }
    };

    // Group expenses by date
    const dailyExpenses = expenses.reduce((acc, item) => {
        const day = item.date.split("T")[0]; // Extracting the date part (yyyy-mm-dd)
        acc[day] = (acc[day] || 0) + item.amount;
        return acc;
    }, {});

    // Chart data to display daily expenses
    const chartData = {
        labels: Object.keys(dailyExpenses),
        datasets: [
            {
                label: "Daily Expense",
                data: Object.values(dailyExpenses),
                borderColor: "red",
                fill: false,
            },
        ],
    };

    return (
        <div className="container">
            <h3>Daily Expense Tracker</h3>

            {/* Display success message */}
            {message && <p className="text-success">{message}</p>}

            {/* Display error message */}
            {error && <p className="text-danger">{error}</p>}

            {/* Input fields for source, amount, and date */}
            <input
                className="input-field"
                placeholder="Expense Source"
                value={source}
                onChange={(e) => setSource(e.target.value)}
            />
            <input
                className="input-field"
                type="number"
                placeholder="Amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
            />
            <input
                className="input-field"
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
            />

            {/* Submit button */}
            <button className="button" onClick={handleSubmit}>
                Add Expense
            </button>

            {/* Chart showing daily expenses */}
            <h4>Expense Chart</h4>
            <br />
            <Line data={chartData} />
        </div>
    );
}
