import { useEffect, useState } from "react";
import Navigation from "../NavBar";
import "./in.css";

export default function Expense() {
    const [ExpenseSource, setExpenseSource] = useState("");
    const [amount, setAmount] = useState("");
    const [reference, setReference] = useState("");
    const [Expenses, setExpenses] = useState([]);
    const [error, setError] = useState("");
    const [message, setMessage] = useState("");
    const [editId, setEditId] = useState(null);
    const [editExpenseSource, setEditExpenseSource] = useState("");
    const [editAmount, setEditAmount] = useState("");
    const [editReference, setEditReference] = useState("");

    const apiUrl = "http://localhost:8080";

    useEffect(() => {
        getItems();
    }, []);

    const getItems = () => {
        fetch(`${apiUrl}/expense`, { method: "GET" })
            .then((res) => res.json())
            .then((res) => setExpenses(res))
            .catch(() => setError("Failed to fetch Expenses"));
    };

    const handleSubmit = () => {
        setError("");

        if (!ExpenseSource.trim() || !amount.trim()) {
            setError("Expense Source and Amount are required.");
            return;
        }

        fetch(`${apiUrl}/expense`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ expense: ExpenseSource, amount: Number(amount), reference }),
        })
            .then((res) => res.json())
            .then(() => {
                setMessage("Expense added successfully");
                setExpenseSource("");
                setAmount("");
                setReference("");
                setTimeout(() => setMessage(""), 3000);
                getItems(); // Refresh list
            })
            .catch(() => setError("Unable to create Expense entry"));
    };

    const handleDelete = (id) => {
        if (window.confirm("Are you sure you want to delete this?")) {
            fetch(`${apiUrl}/expense/${id}`, { method: "DELETE" })
                .then(() => {
                    setMessage("Expense deleted successfully");
                    setTimeout(() => setMessage(""), 3000);
                    getItems(); // Refresh list
                })
                .catch(() => setError("Unable to delete Expense entry"));
        }
    };

    const handleEdit = (item) => {
        setEditId(item._id);
        setEditExpenseSource(item.ExpenseSource);
        setEditAmount(item.Amount);
        setEditReference(item.reference);
    };

    const handleUpdate = () => {
        setError("");

        if (!editExpenseSource.trim() || !editAmount.trim()) {
            setError("Expense Source and Amount are required.");
            return;
        }

        fetch(`${apiUrl}/expense/${editId}`, {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ expense: editExpenseSource, amount: Number(editAmount), reference: editReference }),
        })
            .then((res) => res.json())
            .then(() => {
                setMessage("Expense updated successfully");
                setTimeout(() => setMessage(""), 3000);
                setEditId(null);
                getItems(); // Refresh list
            })
            .catch(() => setError("Unable to update Expense entry"));
    };

    // **Calculate Total Expense**
    const totalExpense = Expenses.reduce((sum, item) => sum + item.Amount, 0);

    return (
        <div>
            <Navigation />
            <div className="container">
                <div className="form-container">
                    <h3>{editId ? "Edit Expense" : "Add Expense"}</h3>
                    {message && <p className="text-success">{message}</p>}
                    <div className="form-group">
                        <input
                            placeholder="Expense Source"
                            value={editId ? editExpenseSource : ExpenseSource}
                            onChange={(e) => (editId ? setEditExpenseSource(e.target.value) : setExpenseSource(e.target.value))}
                            className="form-control"
                        />
                        <input
                            type="number"
                            placeholder="Amount"
                            value={editId ? editAmount : amount}
                            onChange={(e) => (editId ? setEditAmount(e.target.value) : setAmount(e.target.value))}
                            className="form-control"
                        />
                        <input
                            placeholder="Reference (optional)"
                            value={editId ? editReference : reference}
                            onChange={(e) => (editId ? setEditReference(e.target.value) : setReference(e.target.value))}
                            className="form-control"
                        />
                        {editId ? (
                            <>
                                <button className="btn btn-success" onClick={handleUpdate}>
                                    Update
                                </button>
                                <button className="btn btn-secondary" onClick={() => setEditId(null)}>
                                    Cancel
                                </button>
                            </>
                        ) : (
                            <button className="btn btn-dark" onClick={handleSubmit}>
                                Submit
                            </button>
                        )}
                    </div>
                    {error && <p className="text-danger">{error}</p>}
                </div>

                <div className="list-container">
                    <h3>Expense List</h3>
                    <ul className="list-group">
                        {Expenses.map((item) => (
                            <li key={item._id} className="list-group-item d-flex justify-content-between">
                                <span>
                                    {item.ExpenseSource} - ${item.Amount}
                                </span>
                                <div>
                                    <button className="btn btn-warning" onClick={() => handleEdit(item)}>
                                        Edit
                                    </button>
                                    <button className="btn btn-danger" onClick={() => handleDelete(item._id)}>
                                        Delete
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>
                {/* Display Total Expense */}
                <div className="total-Expense">
                    <h4>Total Expense: ${totalExpense.toFixed(2)}</h4>
                </div>
            </div>
        </div>
    );
}
