import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [visitedDates, setVisitedDates] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const today = new Date().toISOString().split('T')[0]; // Current date in YYYY-MM-DD format
  const currentMonth = new Date(today).getMonth();
  const currentYear = new Date(today).getFullYear();

  const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();

  useEffect(() => {
    const fetchVisitedDates = async () => {
      try {
        const res = await axios.get('http://localhost:8080/api/visited-dates');
        setVisitedDates(res.data.map((visit) => visit.date));
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch visited dates.');
        console.error(err);
        setLoading(false);
      }
    };

    const saveVisit = async () => {
      try {
        await axios.post('http://localhost:8080/api/visit', { date: today });
      } catch (err) {
        console.error('Error saving visit:', err);
      }
    };

    saveVisit();
    fetchVisitedDates();
  }, [today]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="App">
      <h1 className='head'>Calendar</h1>
      <div className="calendar">
        {[...Array(daysInMonth).keys()].map((day) => {
          const date = `${currentYear}-${String(currentMonth + 1).padStart(2, '0')}-${String(day + 1).padStart(2, '0')}`;
          const isToday = date === today;
          const isVisited = visitedDates.includes(date);

          return (
            <div
              key={date}
              className={`day ${isToday ? 'today' : ''} ${isVisited ? 'visited' : ''}`}
            >
              {day + 1}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
